import React from 'react'
import styled from 'styled-components';
import ArrowLeftOutlinedIcon from '@mui/icons-material/ArrowLeftOutlined';
import ArrowRightOutlinedIcon from '@mui/icons-material/ArrowRightOutlined';


const Container = styled.div`
width: 100%;
height: 100vh;
display: flex;
position: relative;
overflow: hidden;
`


const Arrow = styled.div`
background-color: #fff;
width: 50px;
height: 50px;
border-radius: 50%;
display: flex;
justify-content: center;
align-items: center;
position: absolute;
top: 0;
bottom: 0;
left: ${props => props.direction === "left" && "10px"};
right: ${props => props.direction === "right" && "10px"};
margin: auto;
cursor: pointer;
opacity:0.6;
`
const Wrapper = styled.div`
height: 100%;
display: flex;
`


const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
  `

const ImgContainer = styled.div`
background-color: orange;
height: 100%;
flex: 1;
`
const Image = styled.img`
height: 80%;
background-color: white;
text-align: center;
`


const InfoContainer = styled.div`
flex:1;
`

const Title = styled.h1``
const Desc = styled.p``
const Button = styled.button``






export const Slider = () => {
    return (
        <Container>
            <Arrow direction="left">
                <ArrowLeftOutlinedIcon></ArrowLeftOutlinedIcon>
            </Arrow>
            <Wrapper>
                <Slide>

                    <ImgContainer>
                        <Image src="https://i.ibb.co/DG69bQ4/2.png" alt="" />
                    </ImgContainer>

                    <InfoContainer>

                        <Title>sdvhnuisdv</Title>
                        <Desc>Lorem ipsum dolor sit amet consectetur adipisicing elit. Incidunt, iure!</Desc>
                        <Button>skjaf</Button>

                    </InfoContainer>

                </Slide>
            </Wrapper>
            <Arrow direction="right">
                <ArrowRightOutlinedIcon></ArrowRightOutlinedIcon>
            </Arrow>

        </Container>
    )
}
